import React, { useState, useEffect } from "react";
import "./App.css";

import Axios from "axios";

function App() {
  const [query, setQuery] = useState("mumbai");
  const [weather, setWeather] = useState({});

  useEffect(() => {
    setWeather(async () => {
      const { data } = await Axios.get(`http://localhost:4545?search=${query}`);
      return data;
    });
  });

  const search = async (e) => {
    if (e.key === "Enter") {
      const { data } = await Axios.get(`http://localhost:4545?search=${query}`);
      setWeather(data);
      setQuery("");
    }
  };

  return (
    <div className="main-container">
      <input
        type="text"
        className="search"
        placeholder="Search.."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        onKeyPress={search}
      />
      {weather && (
        <div className="city">
          <h2 className="city-name">
            <span>{weather.name}</span>
            <sup>{weather.country}</sup>
          </h2>
          <div className="city-temp">
            {weather.temperature}
            <sup>&deg;C</sup>
          </div>
          <div className="info">
            <img
              className="city-icon"
              src={weather.icon}
              alt={weather.description}
            />
            <p>{weather.description}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
